mapper.select:
SELECT mt.sigungu_code AS cd , mt.sigungu_name AS name FROM tb_mtype mt INNER JOIN tb_ltype lt ON mt.sido_code = lt.sido_code
and mt.sido_code = #{lcode}
ORDER_BY mt.sigungu_code

SELECT st.pharm_no AS cd , st.pharm_name AS name FROM tb_stype st INNER JOIN tb_ltype lt ON st.sido_code = lt.sido_code INNER JOIN tb_mtype mt ON st.sigungu_code = mt.sigungu_code
and st.sido_code = #{lcode}
and st.sigungu_code = #{mcode}
ORDER BY st.pharm_no
